function areAnagrams(str1, str2) {
    // pretvorimo oba niza v male črke
    str1 = str1.toLowerCase().replace(/[^a-z]/g, '');
    str2 = str2.toLowerCase().replace(/[^a-z]/g, '');

    // preverimo ali imata oba niza enako število črk
    if (str1.length !== str2.length) {
        return false;
    }

    // pretvorimo črke v nizu v objekt, kjer je ključ črka in vrednost število pojavitev črke
    let charCount1 = {};
    let charCount2 = {};

    for (let char of str1) {
        charCount1[char] = (charCount1[char] || 0) + 1;
    }

    for (let char of str2) {
        charCount2[char] = (charCount2[char] || 0) + 1;
    }

    // preverimo ali imata oba objekta enako število pojavitev črk
    for (let key in charCount1) {
        if (charCount1[key] !== charCount2[key]) {
            return false;
        }
    }

    return true;
}

